methods.method:
function Constructor(argobj)
				{
					AddEvtHandler("EVENT_SLIDE_OPENED", "nav_010_slidename", argobj.pid );
					return true;
				}

function HandleEvt(oEvtHandler)
				{
					if(document.getElementById(oEvtHandler.sTargetId)==null) return false;
					switch (oEvtHandler.sEvt)
					{
						case "EVENT_SLIDE_OPENED":
						{
							document.getElementById(oEvtHandler.sTargetId + "_SlideName").innerHTML = g_oSlide!=null ? g_oSlide.getAttribute("name") : "";
							break;
						}
					}
					return true;
				}

function GetProperty(argobj)
				{
					if(argobj.pid==null || argobj.property==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					switch(argobj.property.toLowerCase())
					{
						case "slidename":	g_vPropertyValue = g_oSlide!=null ? g_oSlide.getAttribute("name") : "";	break;
						default: return true;
					}
					return true;
				}

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				oCurObj.setAttribute("num","");
				AddEvtHandler("EVENT_SLIDE_OPENED", "nav_002_position", argobj.pid);
				return true;
			}

function HandleEvt(oEvtHandler)
			{
				if(oEvtHandler.sTargetId==null) return false;
				var oCurObj = document.getElementById(oEvtHandler.sTargetId);
				if(oCurObj==null) return false;
				switch (oEvtHandler.sEvt)
				{
					case "EVENT_SLIDE_OPENED":
					{
						// Detecting structure object
						var sCurrentSlideId = g_oSlide.getAttribute("id");
						var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
						var bStructure = (oStructure!=null);
						var iCurrent = 0;
						var sCurrent = "";
						if(bStructure)
						{
							var sStructureId = oStructure.getAttribute("id");
							var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId +  "']");
							if(oStructureParams==null) return false;

							var aChapters = oStructureParams.selectNodes("chapter_list/item");
							var aSlideStrip = new Array();
							var aChapterSlides;
							var iIndex = 1;
							for(var i=0; i<aChapters.length; i++)
							{
								sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
								if(sId!="")
								{
									aSlideStrip.push(sId);
									if(sId==sCurrentSlideId) iCurrent = iIndex;
									iIndex++;
								}
								aChapterSlides = aChapters[i].selectNodes("slide_list/item");
								for(var j=0; j<aChapterSlides.length; j++)
								{
									sId = aChapterSlides[j]. selectSingleNode("slide_id").text;
									if(sId!="")
									{
										aSlideStrip.push(sId);
										if(sId==sCurrentSlideId) iCurrent = iIndex;
										iIndex++;
									}
								}
							}
							sTotal = aSlideStrip.length.toString();
							sCurrent = iCurrent.toString();
							oCurObj.setAttribute("num", sCurrent);
							document.getElementById(oEvtHandler.sTargetId + "_position_total").innerHTML = sTotal;
							document.getElementById(oEvtHandler.sTargetId + "_position_current").innerHTML = sCurrent;
						}
						else
						{
							var aSlides = g_oSlides.selectNodes("slide");
							sTotal = aSlides.length.toString();
							document.getElementById(oEvtHandler.sTargetId + "_position_total").innerHTML = sTotal;
							for(var i=0; i<aSlides.length; i++)
							{
								iCurrent++;
								var oSlide = aSlides[i];
								if(oSlide.getAttribute("id")==sCurrentSlideId) break;
							}
							sCurrent = iCurrent.toString();
							oCurObj.setAttribute("num", sCurrent);
							document.getElementById(oEvtHandler.sTargetId + "_position_current").innerHTML = sCurrent;
						}
						break;
					}
				}
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				switch(argobj.property.toLowerCase())
				{
					case "currentposition":        g_vPropertyValue = oCurObj.getAttribute("num");        break;
					default: return true;
				}
				return true;
			}

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oCurSelector = document.getElementById(argobj.pid + "_selector");
				if(oCurSelector==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid +  "']");
				if(oParams==null) return false;

				var bHierarchy = (oParams.selectSingleNode("hierarchy").text=="yes");
				var bNavLock = (oParams.selectSingleNode("nav_lock").text=="yes");

				// Detecting structure object
				var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
				var bStructure = (oStructure!=null);

				var oOptions = oCurSelector.options;
				while(oOptions.length>0) oCurSelector.remove(0);

				var oSlides = g_oSlides.selectNodes("slide");
				var oSlide;
				var sName = "";
				var sId = "";
				var sIndent = "";
				var oOption;
				var bStructureSuccess = false;

				if(bStructure)
				{
					bStructureSuccess = true;
					var sStructureId = oStructure.getAttribute("id");
					var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId + "']");
					var aChapters = oStructureParams.selectNodes("chapter_list/item");
					var aSlides;
					var sChapterNumber = "";
					var sChapterName = "";
					var sTitleSlide = "";
					var sFinalSlide = "";

					for(var i=0; i<aChapters.length; i++)
					{
						sChapterNumber = aChapters[i].selectSingleNode("chapter_number").text;
						sChapterName = aChapters[i].selectSingleNode("chapter_name").text;
						sTitleSlide = aChapters[i].selectSingleNode("chapter_title_slide").text;
						if(sTitleSlide!="")
						{
							oSlide = g_oSlides.selectSingleNode("slide[@id='" + sTitleSlide + "']");
							if(oSlide!=null)
							{
								sId = oSlide.getAttribute("id");
								sName = sChapterNumber + ". " + oSlide.getAttribute("name");
								oOption = document.createElement("option");
								oCurSelector.appendChild(oOption);
								oOption.text = sName;
								oOption.value = sId;
							}
						}
						aSlides = aChapters[i].selectNodes("slide_list/item");
						for(var j=1; j<=aSlides.length; j++)
						{
							sId = aSlides[j-1].selectSingleNode("slide_id").text;
							oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
							if(oSlide==null) continue;
							sName = sChapterNumber + "." + j.toString() + " " + oSlide.getAttribute("name");
							oOption = document.createElement("option");
							oCurSelector.appendChild(oOption);
							oOption.text = sName;
							oOption.value = sId;
						}
					}
				}

				if(!bStructureSuccess)
				{
					for(var i=0; i<oSlides.length;i++)
					{
						oSlide = oSlides[i];
						sId = oSlide.getAttribute("id");
						sIndent = oSlide.getAttribute("indent");
						sName = "";
						if(sIndent!=null && bHierarchy)
						{
							var nIndent = parseInt(sIndent);
							for(var j=0; j<nIndent; j++) sName += "-";
							sName += " ";
						}
						sName += oSlide.getAttribute("name");
						oOption = document.createElement("option");
						oCurSelector.appendChild(oOption);
						oOption.text = sName;
						oOption.value = sId;
					}
				}
				oCurObj.setAttribute("state","enabled");
				oCurObj.setAttribute("navig","0");

				AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_001_selector", argobj.pid);
				AddEvtHandler("EVENT_SLIDE_OPENED", "nav_001_selector", argobj.pid);
				AddEvtHandler("EVENT_NAVIGATION", "nav_001_selector", argobj.pid);
				return true;
			}

function HandleEvt(oEvtHandler)
			{
				var argobj = { pid: oEvtHandler.sTargetId };
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
				if(oParams==null) return false;
				var bNavLock = (oParams.selectSingleNode("nav_lock").text=="yes");
				var sCurrentSlideId = g_oSlide.getAttribute("id");
				var sSavedSlideId = oCurObj.getAttribute("sid");
				if(sSavedSlideId==null) sSavedSlideId = "";
				switch(oEvtHandler.sEvt)
				{
					case "EVENT_SLIDE_OPENED":
					{
						if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid", "");
						var oCurSelector = document.getElementById(argobj.pid + "_selector");
						var oOptions = oCurSelector.options;
						for(var i=0; i<oOptions.length; i++)
						{
							if(oOptions[i].value == sCurrentSlideId)
							{
								oCurSelector.selectedIndex = i;
								oOptions[i].setAttribute("selected","1");
							}
							else
							{
								oOptions[i].removeAttribute("selected");
							}
						}
						if(bNavLock) CallMethod("nav_001_selector","DisableSelector",argobj);
						break;
					}
					case "EVENT_SLIDE_COMPLETE":
					{
						if(oCurObj.getAttribute("state")=="disabled" && sSavedSlideId=="")
						{
							oCurObj.setAttribute("sid","");
							CallMethod("nav_001_selector", "EnableSelector", argobj);
						}
						break;
					}
					case "EVENT_NAVIGATION":
					{
						if(oArg==null) return false;
						if(oArg.sTargets=="selector" || oArg.sTargets=="all")
						{
							if(oArg.sAction=="on" && oCurObj.getAttribute("state")=="disabled")
							{
								oCurObj.setAttribute("sid","");
								CallMethod("nav_001_selector", "EnableSelector", argobj);
							}
							if(oArg.sAction=="off" && oCurObj.getAttribute("state")=="enabled")
							{
								oCurObj.setAttribute("sid", sCurrentSlideId);
								CallMethod("nav_001_selector", "DisableSelector", argobj);
							}
						}
						break;
					}
				}
				return true;
			}

function DisableSelector(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.getAttribute("state")=="disabled") return false;
				var oCurSelector = document.getElementById(argobj.pid + "_selector");
				if(oCurSelector==null) return false;
				oCurSelector.disabled = true;
				oCurObj.setAttribute("state", "disabled");
				return true;
			}

function EnableSelector(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.getAttribute("state")=="enabled") return false;
				var oCurSelector = document.getElementById(argobj.pid + "_selector");
				if(oCurSelector==null) return false;
				oCurSelector.disabled = false;
				oCurObj.setAttribute("state", "enabled");
				return true;
			}

function GetProperty(argobj)
			{
				if(argobj.pid==null || argobj.property==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oCurSelector = document.getElementById(argobj.pid + "_selector");
				if(oCurSelector==null) return false;
				var oOptions = oCurSelector.options;
				if(oOptions==null) return true;
				switch(argobj.property.toLowerCase())
				{
					case "disabled":        g_vPropertyValue = oCurSelector.disabled ? "1" : "0";            break;
					case "selectedid":        g_vPropertyValue = oOptions[oCurSelector.selectedIndex].value;     break;
					case "selectedname":    g_vPropertyValue = oOptions[oCurSelector.selectedIndex].text;     break;
					case "selectednumber":    g_vPropertyValue = oCurSelector.selectedIndex+1;     break;
					case "totalentries":    g_vPropertyValue = oOptions.length;     break;
					default: return true;
				}
				return true;
			}

function JumpToSlide(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oCurSelector = document.getElementById(argobj.pid + "_selector");
				if(oCurSelector==null) return false;
				var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
				if(oParams==null) return false;

				// Detecting structure object
				var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
				var bStructure = (oStructure!=null);

				var sMsg = oParams.selectSingleNode("msg").text;
				if(sMsg==null) sMsg = "You cannot jump to this slide until you visit previous slides";

				var oOptions = oCurSelector.options;
				var sJumpTo = oOptions[oCurSelector.selectedIndex].value;
				var sCurrentSlideId = g_oSlide.getAttribute("id");

				if(bStructure)
				{
					var sStructureId = oStructure.getAttribute("id");
					var oStructureArgs = { pid: sStructureId, current: sCurrentSlideId, target: sJumpTo };
					CallMethod("nav_025_structure", "CheckAllowance", oStructureArgs);
					if(g_arMethodReturnValue[sStructureId]==false)
					{
						g_arMethodReturnValue[sStructureId] = null;
						var    oSlides = g_oSlides.selectNodes("slide");
						var sId;
						for(var i=0; i<oOptions.length;i++)
						{
							if(oOptions[i].value==sCurrentSlideId)
							{
								oCurSelector.selectedIndex = i;
								break;
							}
						}
						alert(sMsg);
						return false;
					}
					g_arMethodReturnValue[sStructureId] = null;
					OpenSlideById(sJumpTo);
					return true;
				}

				if(g_bStrictOrder)
				{
					var oSlide = g_oSlides.selectSingleNode("slide[@id='" + sJumpTo + "']");
					if(oSlide==null) return false;
					if(!SCOIsSlideVisited(oSlide))
					{
						var oTmpSlide = GetSibling(oSlide, "prev");
						if(oTmpSlide!=null)
						{
							if(!SCOIsSlideVisited(oTmpSlide))
							{
								var    oSlides = g_oSlides.selectNodes("slide");
								var sId;
								for(var i=0; i<oOptions.length;i++)
								{
									if(oOptions[i].value==sCurrentSlideId)
									{
										oCurSelector.selectedIndex = i;
										break;
									}
								}
								alert(sMsg);
								return false;
							}
						}
					}
				}
				OpenSlideById(sJumpTo);
				if(oParams.selectSingleNode("sound.enabled").text=="yes") PlayObjectSound( { pid: argobj.pid, snd: "click" } );
				return true;
			}

function Constructor(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;
            var bFirstRun = (oCurObj.getAttribute("firstrun")==null);
            var oCurContents = document.getElementById(argobj.pid + "_contents");
            var bStandard = (oParams.selectSingleNode("standard").text=="yes");
            var bHierarchy = (oParams.selectSingleNode("hierarchy").text=="yes");
            var sNavLock = oParams.selectSingleNode("nav_lock").text=="yes" ? "1" : "0";
            // Detecting structure object
            var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
            var bStructure = (oStructure!=null);

            if(bFirstRun)
            {
                oCurObj.setAttribute("navlock", sNavLock);
                oCurObj.setAttribute("firstrun", "1");
                oCurObj.setAttribute("state", "enabled");
                oCurObj.setAttribute("winstatus", "closed");
                if(document.getElementById(argobj.pid+"_header").getElementsByTagName("p").length!=0)
                {
                    //fixing Gecko-specific p rendering
                    var aTmp = document.getElementById(argobj.pid+"_header").getElementsByTagName("p");
                    for(var i=0;i<aTmp.length;i++)
                    {
                        aTmp[i].style.padding = "0px";
                        aTmp[i].style.margin = "0px";
                    }
                }
            }
            if(document.getElementById(argobj.pid+"_contents_window_clone")==null)
            {
                var oObj = document.getElementById(argobj.pid+"_contents_window_div").cloneNode(true);
                oObj.setAttribute("id", argobj.pid+"_contents_window_clone");
                document.getElementById("boardFrame").appendChild(oObj);
            }
            oCurContents = oObj.getElementsByTagName("div").item(0);
            oCurContents.setAttribute("id", argobj.pid+"_contents");
            oCurContents.innerHTML = "";
            var    oSlides = g_oSlides.selectNodes("slide");
            var oSlide;
            var sName = "";
            var sId = "";
            var sIndent = "";
            var oTmpl;
            var oClone;
            var aTags;
            var aImgs;
            var oIconOpen = document.getElementById(argobj.pid + "_icon_open");
            var oIconClosed = document.getElementById(argobj.pid + "_icon_closed");

            if(bStructure)
            {
                var sStructureId = oStructure.getAttribute("id");
                var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId +  "']");
                if(oStructureParams==null) return false;
                var oTemplateChapter = document.getElementById(argobj.pid + "_template_chapter");
                var oTemplateItem = document.getElementById(argobj.pid + "_template_item");
                if(oTemplateChapter==null || oTemplateItem==null) return false;
                var oChildDiv;
                var oParentDiv;

                var aChapters = oStructureParams.selectNodes("chapter_list/item");
                var aChapterSlides;
                var iIndex = 0;
                var bTitleSlide = false;
                for(var i=0; i<aChapters.length; i++)
                {
                    oParentDiv = document.createElement("div");
                    oParentDiv.setAttribute("id", argobj.pid + "_parent_" + i.toString());
                    oParentDiv.setAttribute("childdiv", argobj.pid + "_child_" + i.toString());
                    oCurContents.appendChild(oParentDiv);
                    oClone = oTemplateChapter.cloneNode(true);
                    oClone.style.display = g_isMSIE ? "inline" : "block";
                    aTags = oClone.getElementsByTagName("td");
                    aImgs = oClone.getElementsByTagName("img");
                    sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
                    sName = aChapters[i].selectSingleNode("chapter_name").text;
                    bTitleSlide = false;
                    if(sId!="")
                    {
                        oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
                        if(oSlide!=null)
                        {
                            bTitleSlide = true;
                            if(sName=="") sName = oSlide.getAttribute("name");
                        }
                    }
                    if(bTitleSlide && !g_bStrictOrder)
                    {
                        aTags[2].innerHTML = '<a href="#" onclick="var oArgs = { pid: \''+argobj.pid+'\', sid: \''+sId+'\' }; CallMethod(\'nav_021_contents\',\'JumpToSlide\',oArgs); return false;">'+sName+'</a>';
                        oIcon = aImgs[1];
                        oIcon.style.display = g_isMSIE ? "inline" : "block";
                    }
                    else
                    {
                        aTags[2].innerHTML = sName;
                    }
                    oClone.setAttribute("iindex", iIndex.toString());
                    oClone.setAttribute("id", argobj.pid + "_item_" + iIndex.toString());
                    aImgs[0].setAttribute("childdiv", argobj.pid + "_child_" + i.toString());
                    aImgs[0].setAttribute("iconopen", oIconOpen.src);
                    aImgs[0].setAttribute("iconclosed", oIconClosed.src);
                    oParentDiv.appendChild(oClone);
                    iIndex++;

                    aChapterSlides = aChapters[i].selectNodes("slide_list/item");
                    oChildDiv = document.createElement("div");
                    oChildDiv.setAttribute("id", argobj.pid + "_child_" + i.toString());
                    oCurContents.appendChild(oChildDiv);
                    for(var j=0; j<aChapterSlides.length; j++)
                    {
                        sId = aChapterSlides[j]. selectSingleNode("slide_id").text;
                        if(sId!="")
                        {
                            oClone = oTemplateItem.cloneNode(true);
                            oClone.style.display = g_isMSIE ? "inline" : "block";
                            oClone.setAttribute("iindex", iIndex.toString());
                            oClone.setAttribute("id", argobj.pid + "_item_" + iIndex.toString());
                            oChildDiv.appendChild(oClone);
                            aTags = oClone.getElementsByTagName("td");
                            oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
                            sName = oSlide.getAttribute("name");
                            aTags[1].innerHTML = !g_bStrictOrder ? '<a href="#" onclick="var oArgs = { pid: \''+argobj.pid+'\', sid: \''+sId+'\' }; CallMethod(\'nav_021_contents\',\'JumpToSlide\',oArgs); return false;">'+sName+'</a>' : sName;
                            iIndex++;
                            aTags = oClone.getElementsByTagName("img");
                            oIcon = aTags[0];
                            oIcon.style.display = g_isMSIE ? "inline" : "block";
                        }
                    }
                }
            }
            else
            {
                if(!bHierarchy) {
                    oTmpl = document.getElementById(argobj.pid+"_template_simple");
                    for(var i=0; i<oSlides.length;i++) {
                        oSlide = oSlides[i];
                        sName = oSlide.getAttribute("name");
                        sId = oSlide.getAttribute("id");
                        oClone = oTmpl.cloneNode(true);
                        aTags = oClone.getElementsByTagName("img");
                        aTags[0].setAttribute("id",argobj.pid+"_icon_"+sId);
                        aTags = oClone.getElementsByTagName("td");
                        aTags[1].setAttribute("id", argobj.pid+"_td_"+sId);
                        aTags[1].innerHTML = sName;
                        if(i==oSlides.length-1) {
                            oClone.style.borderBottom = oClone.style.borderTop;
                            oClone.style.paddingBottom = "1px";
                        }
                        oCurContents.appendChild(oClone);
                        oClone.style.display = g_isMSIE ? "inline" : "block";
                    }
                } else {
                    oTmpl = document.getElementById(argobj.pid+"_template_hier");
                    var nIndent = 0;    var nNextIndent = 0;    var nPrevIndent = 0;
                    var oSlide = null;    var oNextSlide = null;    var oPrevSlide = null;
                    var oCurDiv = oCurContents;
                    var aDivs = new Array();
                    pushArray(aDivs,oCurDiv);
                    var oTmp;
                    for(var i=0; i<oSlides.length;i++) {
                        oSlide = oSlides[i];
                        sName = oSlide.getAttribute("name");
                        sId = oSlide.getAttribute("id");
                        nIndent = oSlide.getAttribute("indent");
                        if(nIndent!=0 && i==0) nIndent = 0;
                        if(nIndent>nPrevIndent) {
                            oCurDiv = document.createElement("div");
                            oCurDiv.setAttribute("id", argobj.pid+"_div_"+oPrevSlide.getAttribute("id"));
                            pushArray(aDivs,oCurDiv);
                        } else {
                            while(nIndent<nPrevIndent) {
                                oTmp = popArray(aDivs);
                                oCurDiv = popArray(aDivs);
                                oCurDiv.appendChild(oTmp);
                                pushArray(aDivs,oCurDiv);
                                nPrevIndent--;
                            }
                        }
                        oNextSlide = (i==oSlides.length-1) ? null : oSlides[i+1];
                        nNextIndent = (oNextSlide==null) ? 0 : oNextSlide.getAttribute("indent");
                        oClone = oTmpl.cloneNode(true);
                        oClone.setAttribute("id", argobj.pid+"_item_"+sId)
                        aTags = oClone.getElementsByTagName("img");
                        if(nNextIndent>nIndent) {
                            aTags[0].style.cursor = g_isMSIE ? "hand" : "pointer";
                            aTags[0].setAttribute("childdiv", argobj.pid+"_div_"+sId);
                            aTags[0].setAttribute("iconopen", document.getElementById(argobj.pid+"_icon_open").src);
                            aTags[0].setAttribute("iconclosed", document.getElementById(argobj.pid+"_icon_closed").src);
                        } else {
                            aTags[0].style.visibility = "hidden";
                        }
                        aTags[1].setAttribute("id",argobj.pid+"_icon_"+sId);
                        aTags = oClone.getElementsByTagName("td");
                        aTags[2].setAttribute("id", argobj.pid+"_td_"+sId);
                        aTags[2].innerHTML = sName;
                        if(nIndent!=0) {
                            var z = aTags[0].parentNode;
                            var x = aTags[1];
                            var y = aTags[0].cloneNode(true);
                            for(var j=0;j<nIndent;j++) {
                                y = aTags[0].cloneNode(true);
                                x = z.insertBefore(y,x);
                            }
                        }
                        oClone.style.display = g_isMSIE ? "inline" : "block";
                        if(i==oSlides.length-1) {
                            oClone.style.borderBottom = oClone.style.borderTop;
                            oClone.style.paddingBottom = "1px";
                        }
                        oCurDiv.appendChild(oClone);
                        oPrevSlide = oSlide;
                        nPrevIndent = nIndent;
                    }
                    if(aDivs.length>0) {
                        nIndent = 0;
                        while(nIndent<nPrevIndent) {
                            oTmp = popArray(aDivs);
                            oCurDiv = popArray(aDivs);
                            oCurDiv.appendChild(oTmp);
                            pushArray(aDivs,oCurDiv);
                            nPrevIndent--;
                        }
                    }
                }
            }
            if(bFirstRun) {
                AddEvtHandler("EVENT_SLIDE_OPENED", "nav_021_contents", argobj.pid);
                AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_021_contents", argobj.pid);
                AddEvtHandler("EVENT_NAVIGATION", "nav_021_contents", argobj.pid);
            }
            return true;
        }

function HandleEvt(oEvtHandler, oArg)
        {
            var argobj = new Object;
            argobj.pid = oEvtHandler.sTargetId;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var sCurrentSlideId = g_oSlide.getAttribute("id");
            var sSavedSlideId = oCurObj.getAttribute("sid");
            if(sSavedSlideId==null) sSavedSlideId = "";
            switch (oEvtHandler.sEvt) {
                case "EVENT_SLIDE_OPENED":
                    if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
                    if(oCurObj.getAttribute("closed")=="0") CallMethod("nav_021_contents","ToggleWindow",argobj);
                    if(oCurObj.getAttribute("navlock")=="1") CallMethod("nav_021_contents","DisableAccess",argobj);
                    break;
                case "EVENT_SLIDE_COMPLETE":
                    if(oCurObj.getAttribute("state")=="disabled" && sSavedSlideId=="") {
                        oCurObj.setAttribute("sid","");
                        CallMethod("nav_021_contents","EnableAccess",argobj);
                    }
                    break;
                case "EVENT_NAVIGATION":
                    if(oArg==null) return false;
                    if(oArg.sTargets=="contents" || oArg.sTargets=="all") {
                        if(oArg.sAction=="on" && oCurObj.getAttribute("state")=="disabled") {
                            oCurObj.setAttribute("sid","");
                            CallMethod("nav_021_contents","EnableAccess",argobj);
                        }
                        if(oArg.sAction=="off" && oCurObj.getAttribute("state")=="enabled") {
                            oCurObj.setAttribute("sid",sCurrentSlideId);
                            CallMethod("nav_021_contents","DisableAccess",argobj);
                        }
                    }
                default:
                    break;
            }
            return true;
        }

function ToggleWindow(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(document.getElementById(argobj.pid+"_contents_window_clone")!=null) {
                var oObj = document.getElementById(argobj.pid+"_contents_window_clone");
                if(oObj.style.display != "none") {
                    oObj.style.display = "none";
                    oCurObj.setAttribute("winstatus","closed");
                    document.getElementById(argobj.pid+"_cimg_en").src=document.getElementById(argobj.pid+"_img_en_0").src;
                } else {
                    CallMethod("nav_021_contents","UpdateContents",argobj);
                    oObj.style.display =  g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("winstatus","open");
                }
            } else {
                CallMethod("nav_021_contents","Constructor",argobj);
                CallMethod("nav_021_contents","UpdateContents",argobj);
                var oObj = document.getElementById(argobj.pid+"_contents_window_clone");
                oObj.style.display =  g_isMSIE ? "inline" : "block";
                oCurObj.setAttribute("winstatus","open");
            }
            CallMethod("nav_021_contents", "PlaySound", { pid: argobj.pid, snd: "click" });
            return true;
        }

function DisableAccess(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
            if(!bEnabled) return false;
            if(document.getElementById(argobj.pid+"_contents_window_clone")!=null) {
                if(document.getElementById(argobj.pid+"_contents_window_clone").style.display != "none") document.getElementById(argobj.pid+"_contents_window_clone").style.display = "none";
            }
            document.getElementById(argobj.pid+"_contents_en").style.display = "none";
            document.getElementById(argobj.pid+"_contents_dis").style.display = g_isMSIE ? "inline" : "block";
            oCurObj.setAttribute("state","disabled");
            return true;
        }

function EnableAccess(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
            if(bEnabled) return false;
            document.getElementById(argobj.pid+"_contents_dis").style.display = "none";
            document.getElementById(argobj.pid+"_contents_en").style.display = g_isMSIE ? "inline" : "block";
            document.getElementById(argobj.pid+"_cimg_en").src=document.getElementById(argobj.pid+"_img_en_0").src;
            oCurObj.setAttribute("state","enabled");
            return true;
        }

function UpdateContents(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var    oSlides = g_oSlides.selectNodes("slide");
            var oSlide,sName,sId,sIndent,oTmpl,clone,aTags;
            var bPrevVisited = true;
            var sCurrentSlideId = g_oSlide.getAttribute("id");
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']")
            // Detecting structure object
            var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
            var bStructure = (oStructure!=null);
            var sMsg = oParams.selectSingleNode("msg").text;
            if(sMsg==null) sMsg = "You cannot jump to this slide until you visit previous slides";
            var sCurrentSlideId = g_oSlide.getAttribute("id");


            if(bStructure)
            {
                var sStructureId = oStructure.getAttribute("id");
                var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId +  "']");
                if(oStructureParams==null) return false;
                var aChapters = oStructureParams.selectNodes("chapter_list/item");
                var aChapterSlides;
                var iIndex = 0;
                var oDiv;
                var oSlide;
                var bVisited = true;
                var aTags;
                var aLinks;
                var sName = "";
                var oIcon;
                var oIconCurrent = document.getElementById(argobj.pid + "_icon_current");
                var oIconUnvisited = document.getElementById(argobj.pid + "_icon_unvisited");
                var oIconVisited = document.getElementById(argobj.pid + "_icon_visited");
                for(var i=0; i<aChapters.length; i++)
                {
                    sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
                    oDiv = document.getElementById(argobj.pid + "_item_" + iIndex.toString());
                    if(sId!="")
                    {
                        oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
                        aTags = oDiv.getElementsByTagName("td");
                        if(g_bStrictOrder)
                        {
                            aLinks = aTags[2].getElementsByTagName("a");
                            if(bVisited && aLinks.length==0)
                            {
                                sName = aTags[2].innerHTML;
                                aTags[2].innerHTML = '<a href="#" onclick="var oArgs = { pid: \''+argobj.pid+'\', sid: \''+sId+'\' }; CallMethod(\'nav_021_contents\',\'JumpToSlide\',oArgs); return false;">'+sName+'</a>';
                            }
                        }
                        bVisited = SCOIsSlideVisited(oSlide);
                        aTags = oDiv.getElementsByTagName("img");
                        oIcon = aTags[1];
                        oIcon.style.display = g_isMSIE ? "inline" : "block";
                        if(sId==sCurrentSlideId)
                        {
                            oIcon.src = oIconCurrent.src;
                        }
                        else
                        {
                            oIcon.src = bVisited ? oIconVisited.src : oIconUnvisited.src;
                        }
                    }
                    iIndex++;
                    aChapterSlides = aChapters[i].selectNodes("slide_list/item");
                    for(var j=0; j<aChapterSlides.length; j++)
                    {
                        sId = aChapterSlides[j].selectSingleNode("slide_id").text;
                        if(sId!="")
                        {
                            oDiv = document.getElementById(argobj.pid + "_item_" + iIndex.toString());
                            oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
                            aTags = oDiv.getElementsByTagName("td");
                            if(g_bStrictOrder)
                            {
                                aLinks = aTags[1].getElementsByTagName("a");
                                if(bVisited && aLinks.length==0)
                                {
                                    sName = aTags[1].innerHTML;
                                    aTags[1].innerHTML = '<a href="#" onclick="var oArgs = { pid: \''+argobj.pid+'\', sid: \''+sId+'\' }; CallMethod(\'nav_021_contents\',\'JumpToSlide\',oArgs); return false;">'+sName+'</a>';
                                }
                            }
                            bVisited = SCOIsSlideVisited(oSlide);
                            aTags = oDiv.getElementsByTagName("img");
                            oIcon = aTags[0];
                            oIcon.style.display = g_isMSIE ? "inline" : "block";
                            if(sId==sCurrentSlideId)
                            {
                                oIcon.src = oIconCurrent.src;
                            }
                            else
                            {
                                oIcon.src = bVisited ? oIconVisited.src : oIconUnvisited.src;
                            }
                            iIndex++;
                        }
                    }
                }
            }
            else
            {
                for(var i=0; i<oSlides.length;i++) {
                    oSlide = oSlides[i];
                    sName = oSlide.getAttribute("name");
                    sId = oSlide.getAttribute("id");
                    if(sId!=sCurrentSlideId) {
                        if(document.getElementById(argobj.pid+"_icon_"+sId).src==document.getElementById(argobj.pid+"_icon_visited").src) continue;
                        if(!g_bStrictOrder || (g_bStrictOrder && bPrevVisited)) {
                            document.getElementById(argobj.pid+"_td_"+sId).innerHTML = '<a href="#" onclick="var oArgs=new Object; oArgs.pid=\''+argobj.pid+'\'; oArgs.sid=\''+sId+'\'; CallMethod(\'nav_021_contents\',\'JumpToSlide\',oArgs); return false;">'+sName+'</a>';
                        } else {
                            document.getElementById(argobj.pid+"_td_"+sId).innerHTML = sName;
                            document.getElementById(argobj.pid+"_td_"+sId).title = sMsg;
                        }
                        if(SCOIsSlideVisited(oSlide)) {
                            bPrevVisited = true;
                            document.getElementById(argobj.pid+"_icon_"+sId).src = document.getElementById(argobj.pid+"_icon_visited").src;
                        } else {
                            bPrevVisited = false;
                            document.getElementById(argobj.pid+"_icon_"+sId).src = document.getElementById(argobj.pid+"_icon_unvisited").src;
                        }
                    } else {
                        bPrevVisited = true;
                        document.getElementById(argobj.pid+"_icon_"+sCurrentSlideId).src = document.getElementById(argobj.pid+"_icon_current").src;
                        document.getElementById(argobj.pid+"_td_"+sCurrentSlideId).innerHTML = "<b>"+sName+"</b>";
                    }
                }
                if(oParams.selectSingleNode("hierarchy").text=="yes") {
                    var oCurDiv = document.getElementById(argobj.pid+"_item_"+sCurrentSlideId);
                    var oCurContents = document.getElementById(argobj.pid+"_contents");
                    var iCnt = 0;
                    while(oCurDiv!=oCurContents && iCnt<100) {
                        oCurDiv = oCurDiv.parentNode;
                        if(oCurDiv.nodeName.toLowerCase()=="div") {
                            if(oCurDiv.style.display == "none") oCurDiv.style.display = g_isMSIE ? "inline" : "block";
                        }
                        iCnt++;
                    }
                }
            }
            return true;
        }

function JumpToSlide(argobj)
        {
            if(argobj.pid==null || argobj.sid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            oCurObj.setAttribute("closed","1");
            CallMethod("nav_021_contents","ToggleWindow",argobj);
            OpenSlideById(argobj.sid);
            CallMethod("nav_021_contents", "PlaySound", { pid: argobj.pid, snd: "click" });
            return true;
        }

function GetProperty(argobj)
        {
            if(argobj.pid==null || argobj.property==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            switch(argobj.property.toLowerCase()) {
                case "disabled":    g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";    break;
                case "open":        g_vPropertyValue = oCurObj.getAttribute("closed")=="1" ? "0" : "1";    break;
                default: return true;
            }
            return true;
        }

function Over(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                if(oCurObj.getAttribute("winstatus")!="closed") return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                CallMethod("nav_021_contents", "PlaySound", { pid: argobj.pid, snd: "over" });
                return true;
            }

function Out(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                if(oCurObj.getAttribute("winstatus")!="closed") return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                return true;
            }

function Down(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                return true;
            }

function Click(argobj)
            {
                if(argobj.pid==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                CallMethod("nav_021_contents", "ToggleWindow", { pid: argobj.pid });
                return true;
            }

function PlaySound(argobj)
            {
                if(argobj.pid==null || argobj.snd==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
                if(oParams==null) return false;
                if(oParams.selectSingleNode("sound.enabled").text=="yes")
                {
                    try
                    {
                        switch(argobj.snd)
                        {
                            case "over":
                                if(oParams.selectSingleNode("sound.scheme").text=="over" || oParams.selectSingleNode("sound.scheme").text=="overclick")
                                {
                                    var sSoundFile = oParams.selectSingleNode("sound.file.over").text;
                                    if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.over.user").text!="")
                                    {
                                        PlaySoundInternal(oParams.selectSingleNode("sound.file.over.user").text);
                                    }
                                    else
                                    {
                                        var sExtension = ".swf";
                                        if(sSoundFile=="custom") sSoundFile = "over1";
                                        PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
                                    }
                                }
                                break;
                            case "click":
                                if(oParams.selectSingleNode("sound.scheme").text=="click" || oParams.selectSingleNode("sound.scheme").text=="overclick")
                                {
                                    var sSoundFile = oParams.selectSingleNode("sound.file.click").text;
                                    if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.click.user").text!="")
                                    {
                                        PlaySoundInternal(oParams.selectSingleNode("sound.file.click.user").text);
                                    }
                                    else
                                    {
                                        var sExtension = ".swf";
                                        if(sSoundFile=="custom") sSoundFile = "click1";
                                        PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
                                    }
                                }
                                break;
                            default: return true;
                        }
                    }
                    catch(e)
                    {}
                }
                return true;
            }

function Constructor(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					if(document.getElementById("boardFrame")==null) return false;
					var sNavLock = oParams.selectSingleNode("nav_lock").text=="yes" ? "1" : "0";
					oCurObj.setAttribute("navlock", sNavLock);
					oCurObj.setAttribute("state", "enabled");
					oCurObj.setAttribute("winstatus", "closed");
					if(document.getElementById(argobj.pid+"_header").getElementsByTagName("p").length!=0) {
						//fixing Gecko-specific p rendering
						var tmp = document.getElementById(argobj.pid+"_header").getElementsByTagName("p");
						for(var i=0;i<tmp.length;i++) {
							tmp[i].style.padding = "0px";
							tmp[i].style.margin = "0px";
						}
					}
					if(document.getElementById(argobj.pid+"_help_window_clone")==null) {
						var oObj = document.getElementById(argobj.pid+"_help_window_div").cloneNode(true);
						oObj.setAttribute("id", argobj.pid+"_help_window_clone");
						document.getElementById("boardFrame").appendChild(oObj);
					}
					AddEvtHandler("EVENT_SLIDE_OPENED", "nav_020_help", argobj.pid);
					AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_020_help", argobj.pid);
					AddEvtHandler("EVENT_NAVIGATION", "nav_020_help", argobj.pid);
					return true;
				}

function HandleEvt(oEvtHandler, oArg)
				{
					var argobj = new Object;
					argobj.pid = oEvtHandler.sTargetId;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var sCurrentSlideId = g_oSlide.getAttribute("id");
					var sSavedSlideId = oCurObj.getAttribute("sid");
					if(sSavedSlideId==null) sSavedSlideId = "";
					switch (oEvtHandler.sEvt) {
						case "EVENT_SLIDE_OPENED":
							if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
							if(oCurObj.getAttribute("navlock")=="1") CallMethod("nav_020_help","DisableAccess",argobj);
							break;
						case "EVENT_SLIDE_COMPLETE":
							if(oCurObj.getAttribute("state")=="disabled" && oCurObj.getAttribute("navlock")=="1" && sSavedSlideId=="") {
								oCurObj.setAttribute("sid","");
								CallMethod("nav_020_help","EnableAccess",argobj);
							}
							break;
						case "EVENT_NAVIGATION":
							if(oArg==null) return false;
							if(oArg.sTargets=="help" || oArg.sTargets=="all") {
								if(oArg.sAction=="on" && oCurObj.getAttribute("state")=="disabled") {
									oCurObj.setAttribute("sid","");
									CallMethod("nav_020_help","EnableAccess",argobj);
								}
								if(oArg.sAction=="off" && oCurObj.getAttribute("state")=="enabled") {
									oCurObj.setAttribute("sid",sCurrentSlideId);
									CallMethod("nav_020_help","DisableAccess",argobj);
								}
							}
						default:
							break;
					}
					return true;
				}

function ToggleWindow(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					if(document.getElementById(argobj.pid+"_help_window_clone")==null) {
						var oObj = document.getElementById(argobj.pid+"_help_window_div").cloneNode(true);
						oObj.setAttribute("id", argobj.pid+"_help_window_clone");
						document.getElementById("boardFrame").appendChild(oObj);
					} else {
						var oObj = document.getElementById(argobj.pid+"_help_window_clone");
					}
					if(oObj.style.display != "none") {
						oObj.style.display = "none";
						oCurObj.setAttribute("winstatus","closed");
						document.getElementById(argobj.pid+"_himg_en").src = document.getElementById(argobj.pid+"_img_en_0").src;
					} else {
						oObj.style.display =  g_isMSIE ? "inline" : "block";
						oCurObj.setAttribute("winstatus","open");
					}
					CallMethod("nav_020_help", "PlaySound", { pid: argobj.pid, snd: "click" });
					return true;
				}

function DisableAccess(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
					if(!bEnabled) return false;
					if(document.getElementById(argobj.pid+"_help_window_div").style.display != "none") document.getElementById(argobj.pid+"_help_window_div").style.display = "none";
					document.getElementById(argobj.pid+"_help_en").style.display = "none";
					document.getElementById(argobj.pid+"_help_dis").style.display = g_isMSIE ? "inline" : "block";
					oCurObj.setAttribute("state","disabled");
					return true;
				}

function EnableAccess(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
					if(bEnabled) return false;
					document.getElementById(argobj.pid+"_help_dis").style.display = "none";
					document.getElementById(argobj.pid+"_help_en").style.display = g_isMSIE ? "inline" : "block";
					document.getElementById(argobj.pid+"_himg_en").src=document.getElementById(argobj.pid+"_img_en_0").src;
					oCurObj.setAttribute("state","enabled");
					return true;
				}

function GetProperty(argobj)
				{
					if(argobj.pid==null || argobj.property==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					switch(argobj.property.toLowerCase()) {
						case "disabled":	g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";	break;
						case "open":		g_vPropertyValue = oCurObj.getAttribute("winstatus")=="open" ? "1" : "0";	break;
						default: return true;
					}
					return true;
				}

function Over(argobj)
				{
					if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					if(oCurObj.getAttribute("winstatus")!="closed") return false;
					var oButton = argobj.btn;
					var oReplace = document.getElementById(argobj.pid + argobj.rep);
					if(oButton==null || oReplace==null) return false;
					oButton.src = oReplace.src;
					CallMethod("nav_020_help", "PlaySound", { pid: argobj.pid, snd: "over" });
					return true;
				}

