function Constructor(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;
            var bDisplayLetters = oParams.selectSingleNode("display_letters").text=="yes";

            var oObjWin = document.getElementById(argobj.pid+"_object_window_div");
            oObjWin.style.visibility = "hidden";
            oObjWin.style.display = g_isMSIE ? "inline" : "block";

            var iFullHeight = parseInt(oParams.selectSingleNode("tabheight").text,10);
            if(isNaN(iFullHeight)) iFullHeight = oObjWin.offsetHeight;
            var iHeaderHeight = document.getElementById(argobj.pid+"_header").offsetHeight;
            var iLettersHeight = document.getElementById(argobj.pid+"_letters_td").offsetHeight;
            var oResultDiv = document.getElementById(argobj.pid+"_results");
            var oTermsDiv = document.getElementById(argobj.pid+"_terms");

            var iNewHeight = bDisplayLetters ? (iFullHeight - iLettersHeight - iHeaderHeight - 36) : (iFullHeight - iHeaderHeight - 36);
            oTermsDiv.style.height = iNewHeight.toString() + "px";
            oResultDiv.style.height = iNewHeight.toString() + "px";

            oObjWin.style.visibility = "visible";
            oObjWin.style.display = "none";

            var bFirstRun = oCurObj.getAttribute("firstrun")==null ? true : false;
            var bStandard = oParams.selectSingleNode("standard").text=="yes" ? true : false;
            var sNavLock = oParams.selectSingleNode("nav_lock").text=="yes" ? "1" : "0";
            if(bFirstRun)
            {
                var oRow = document.getElementById(argobj.pid+"_letters_row");
                if(bDisplayLetters)
                {
                    var oTemplateCell = document.getElementById(argobj.pid+"_letters_all");
                    var oCell;
                    var aItems = oParams.selectNodes("item_list/item/e_item");
                    var aLetters = new Array();
                    var sLetters = "";
                    var sLetter = "";
                    var sItem = "";
                    for(var i=0;i<aItems.length;i++)
                    {
                        sItem = trim(aItems[i].text, true, true, true, true);
                        sLetter = sItem.charAt(0);
                        sLetter = sLetter.toUpperCase();
                        if(sLetters.indexOf(sLetter)!=-1) continue;
                        sLetters += sLetter;
                        aLetters.push(sLetter);
                    }
                    var aSortedLetters = aLetters.sort();
                    for(var i=0;i<aSortedLetters.length; i++)
                    {
                        oCell = oTemplateCell.cloneNode(true);
                        oCell.innerHTML = aSortedLetters[i];
                        oRow.appendChild(oCell);
                    }
                }
                else
                {
                    oRow.style.display = "none";
                }

                var oDefText = oParams.selectSingleNode("e_default_text");
                if(oDefText!=null)
                {
                    oResultDiv.innerHTML = oDefText.text;
                }
                else
                {
                    oResultDiv.innerHTML = "&nbsp;";
                }
                oCurObj.setAttribute("navlock", sNavLock);
                oCurObj.setAttribute("firstrun", "1");
                oCurObj.setAttribute("state", "enabled");
                oCurObj.setAttribute("winstatus", "closed");
                oCurObj.setAttribute("helpstatus","0");
                if(document.getElementById(argobj.pid+"_header").getElementsByTagName("p").length!=0)
                {
                    //fixing Gecko-specific p rendering
                    var aTmp = document.getElementById(argobj.pid+"_header").getElementsByTagName("p");
                    for(var i=0;i<aTmp.length;i++)
                    {
                        aTmp[i].style.padding = "0px";
                        aTmp[i].style.margin = "0px";
                    }
                }
            }
            if(document.getElementById(argobj.pid+"_object_window_clone")==null)
            {
                var oObj = oObjWin.cloneNode(true);
                oObj.setAttribute("id", argobj.pid+"_object_window_clone");
                document.getElementById("boardFrame").appendChild(oObj);
            }
            else
            {
                var oObj = document.getElementById(argobj.pid+"_object_window_div");
            }

            if(bFirstRun)
            {
                AddEvtHandler("EVENT_SLIDE_OPENED", "nav_021_gloss", argobj.pid);
                AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_021_gloss", argobj.pid);
                AddEvtHandler("EVENT_NAVIGATION", "nav_021_gloss", argobj.pid);
            }
            return true;
        }

function HandleEvt(oEvtHandler, oArg)
        {
            var argobj = new Object;
            argobj.pid = oEvtHandler.sTargetId;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var sCurrentSlideId = g_oSlide.getAttribute("id");
            var sSavedSlideId = oCurObj.getAttribute("sid");
            if(sSavedSlideId==null) sSavedSlideId = "";
            switch (oEvtHandler.sEvt)
            {
                case "EVENT_SLIDE_OPENED":
                    if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
                    if(oCurObj.getAttribute("closed")=="0") CallMethod("nav_021_gloss","ToggleWindow",argobj);
                    if(oCurObj.getAttribute("navlock")=="1") CallMethod("nav_021_gloss","DisableAccess",argobj);
                    break;
                case "EVENT_SLIDE_COMPLETE":
                    if(oCurObj.getAttribute("state")=="disabled" && sSavedSlideId=="")
                    {
                        oCurObj.setAttribute("sid","");
                        CallMethod("nav_021_gloss","EnableAccess",argobj);
                    }
                    break;
                case "EVENT_NAVIGATION":
                    if(oArg==null) return false;
                    if(oArg.sTargets=="search" || oArg.sTargets=="all")
                    {
                        if(oArg.sAction=="on" && oCurObj.getAttribute("state")=="disabled")
                        {
                            oCurObj.setAttribute("sid","");
                            CallMethod("nav_021_gloss","EnableAccess",argobj);
                        }
                        if(oArg.sAction=="off" && oCurObj.getAttribute("state")=="enabled")
                        {
                            oCurObj.setAttribute("sid",sCurrentSlideId);
                            CallMethod("nav_021_gloss","DisableAccess",argobj);
                        }
                    }
                    break;
                default:
                    break;
            }
            return true;
        }

function ToggleWindow(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(document.getElementById(argobj.pid+"_object_window_clone")!=null)
            {
                var oObj = document.getElementById(argobj.pid+"_object_window_clone");
                if(oObj.style.display != "none")
                {
                    oObj.style.display = "none";
                    oCurObj.setAttribute("winstatus","closed");
                    document.getElementById(argobj.pid+"_button_img_enabled").src = document.getElementById(argobj.pid+"_img_enabled_0").src;
                }
                else
                {
                    oObj.style.display =  g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("winstatus","open");
                }
            }
            else
            {
                CallMethod("nav_021_gloss","Constructor",argobj);
                var oObj = document.getElementById(argobj.pid+"_object_window_clone");
                oObj.style.display =  g_isMSIE ? "inline" : "block";
                oCurObj.setAttribute("winstatus","open");
            }
            CallMethod("nav_021_gloss", "PlaySound", { pid: argobj.pid, snd: "click" });
            return true;
        }

function DisableAccess(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
            if(!bEnabled) return false;
            if(document.getElementById(argobj.pid+"_object_window_clone")!=null)
            {
                if(document.getElementById(argobj.pid+"_object_window_clone").style.display != "none") document.getElementById(argobj.pid+"_object_window_clone").style.display = "none";
            }
            document.getElementById(argobj.pid+"_button_enabled").style.display = "none";
            document.getElementById(argobj.pid+"_button_disabled").style.display = g_isMSIE ? "inline" : "block";
            oCurObj.setAttribute("state","disabled");
            return true;
        }

function EnableAccess(argobj)
        {
            if(argobj.pid==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
            if(bEnabled) return false;
            document.getElementById(argobj.pid+"_button_disabled").style.display = "none";
            document.getElementById(argobj.pid+"_button_enabled").style.display = g_isMSIE ? "inline" : "block";
            document.getElementById(argobj.pid+"_button_img_enabled").src=document.getElementById(argobj.pid+"_img_enabled_0").src;
            oCurObj.setAttribute("state","enabled");
            return true;
        }

function Click(argobj)
        {
            if(argobj.pid==null || argobj.elem==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var aoItems = oParams.selectNodes("item_list/item");
            if(aoItems.length==0) return false;
            var oElem = argobj.elem;
            var sState = oElem.getAttribute("curstate");
            if(sState=="sel") return false;

            // deselect any other
            var oArgs = new Object;
            oArgs.pid = argobj.pid;
            oArgs.target = "items";
            CallMethod("nav_021_gloss","Reset",oArgs);

            oElem.setAttribute("curstate", "sel");

            oElem.style.fontFamily = oParams.selectSingleNode("selected_font").text;
            oElem.style.fontSize = oParams.selectSingleNode("selected_font_size").text + "px";
            oElem.style.fontWeight = oParams.selectSingleNode("selected_font_weight").text;
            oElem.style.fontStyle = oParams.selectSingleNode("selected_font_style").text;
            oElem.style.textDecoration = oParams.selectSingleNode("selected_font_underline").text;
            oElem.style.color = oParams.selectSingleNode("selected_font_color").text;
            oElem.style.backgroundColor = oParams.selectSingleNode("selected_bg_color").text;
            oElem.style.borderColor = oParams.selectSingleNode("selected_border_color").text;
            oElem.style.borderStyle = oParams.selectSingleNode("selected_border_style").text;
            oElem.style.borderWidth = oParams.selectSingleNode("selected_border_width").text + "px";
            var sBGImage = oParams.selectSingleNode("selected_bg_img").text;
            if(sBGImage!="")    oElem.style.backgroundImage = "url(" + sBGImage + ")";
            oElem.style.cursor = "default";

            var sText = trim(oElem.innerHTML, true, true, true, true);
            var oItem = oParams.selectSingleNode("item_list/item[e_item/text()='" + sText + "']");

            if(oItem==null)
            {
                sText = oParams.selectSingleNode("e_default_text").text;
            }
            else
            {
                sText = oItem.selectSingleNode("e_text").text;
            }

            var oObj = document.getElementById(argobj.pid+"_object_window_clone");
            aDivs = oObj.getElementsByTagName("div");
            var oDiv;
            for(var i=aDivs.length-1; i>=0; i--)
            {
                if(aDivs[i].getAttribute("cl_id")=="results")
                {
                    aDivs[i].innerHTML = sText;
                    break;
                }
            }
            CallMethod("nav_021_gloss", "PlaySound", { pid: argobj.pid, snd: "click" });
            return true;
        }

function Filter(argobj)
        {
            if(argobj.pid==null || argobj.elem==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var oElem = argobj.elem;
            var sState = oElem.getAttribute("curstate");
            if(sState=="sel") return false;

            // deselect any other
            var oArgs = new Object;
            oArgs.pid = argobj.pid;
            oArgs.target = "items_letters";
            CallMethod("nav_021_gloss","Reset",oArgs);

            oElem.setAttribute("curstate", "sel");

            oElem.style.fontFamily = oParams.selectSingleNode("selected_font").text;
            oElem.style.fontSize = oParams.selectSingleNode("selected_font_size").text + "px";
            oElem.style.fontWeight = oParams.selectSingleNode("selected_font_weight").text;
            oElem.style.fontStyle = oParams.selectSingleNode("selected_font_style").text;
            oElem.style.textDecoration = oParams.selectSingleNode("selected_font_underline").text;
            oElem.style.color = oParams.selectSingleNode("selected_font_color").text;
            oElem.style.backgroundColor = oParams.selectSingleNode("selected_bg_color").text;
            oElem.style.borderColor = oParams.selectSingleNode("selected_border_color").text;
            oElem.style.borderStyle = oParams.selectSingleNode("selected_border_style").text;
            oElem.style.borderWidth = oParams.selectSingleNode("selected_border_width").text + "px";
            var sBGImage = oParams.selectSingleNode("selected_bg_img").text;
            if(sBGImage!="")    oElem.style.backgroundImage = "url(" + sBGImage + ")";
            oElem.style.cursor = "default";

            var sLetter = trim(oElem.innerHTML,true, true, true, true);

            var oObj = document.getElementById(argobj.pid+"_object_window_clone");
            var aDivs = oObj.getElementsByTagName("div");
            var oResDiv;
            var oTermsDiv;
            for(var i=0; i<aDivs.length; i++)
            {
                if(aDivs[i].getAttribute("cl_id")=="results")
                {
                    oResDiv = aDivs[i];
                    continue;
                }
                if(aDivs[i].getAttribute("cl_id")=="terms")
                {
                    oTermsDiv = aDivs[i];
                    continue;
                }
            }

            aDivs = oTermsDiv.getElementsByTagName("div");
            var sStr = "";
            var oFirstDiv;
            var bFirstItem = false;
            for(var i=0; i<aDivs.length; i++)
            {
                if(sLetter=="*" || sLetter=="")
                {
                    aDivs[i].style.display = g_isMSIE ? "inline" : "block";
                    if(!bFirstItem)
                    {
                        var oArgs = new Object;
                        oArgs.pid = argobj.pid;
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","");
                        CallMethod("nav_021_gloss","Click",oArgs);
                        bFirstItem = true;
                    }
                }
                else
                {
                    sStr = trim(aDivs[i].innerHTML,true,true,true,true);
                    sStr = sStr.charAt(0);
                    sStr = sStr.toUpperCase();
                    if(sLetter==sStr)
                    {
                        aDivs[i].style.display = g_isMSIE ? "inline" : "block";
                        if(!bFirstItem)
                        {
                            var oArgs = new Object;
                            oArgs.pid = argobj.pid;
                            oArgs.elem = aDivs[i];
                            aDivs[i].setAttribute("curstate","");
                            CallMethod("nav_021_gloss","Click",oArgs);
                            bFirstItem = true;
                        }
                    }
                    else
                    {
                        aDivs[i].style.display = "none";
                    }
                }
            }
            return true;
            }

function DisplayItem(argobj)
        {
            if(argobj.pid==null || argobj.ecode==null || argobj.eitem==null) return false;
            if(argobj.ecode=="" && argobj.eitem=="") return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var oObj = document.getElementById(argobj.pid+"_object_window_clone");
            if(oObj==null)
            {
                var oObjWin = document.getElementById(argobj.pid+"_object_window_div");
                var oObj = oObjWin.cloneNode(true);
                oObj.setAttribute("id", argobj.pid+"_object_window_clone");
                document.getElementById("boardFrame").appendChild(oObj);
            }

            // deselect any other
            var oArgs = new Object;
            oArgs.pid = argobj.pid;
            oArgs.target = "items_letters";
            CallMethod("nav_021_gloss","Reset",oArgs);

            if(oObj.style.display == "none") CallMethod("nav_021_gloss","ToggleWindow",oArgs);

            var aDivs = oObj.getElementsByTagName("div");
            var oResDiv;
            var oTermsDiv;
            for(var i=0; i<aDivs.length; i++)
            {
                if(aDivs[i].getAttribute("cl_id")=="results")
                {
                    oResDiv = aDivs[i];
                    oResDiv.innerHTML = oParams.selectSingleNode("not_found").text;
                    continue;
                }
                if(aDivs[i].getAttribute("cl_id")=="terms")
                {
                    oTermsDiv = aDivs[i];
                    continue;
                }
            }
            aDivs = oTermsDiv.getElementsByTagName("div");

            for(var i=0; i<aDivs.length; i++)
            {
                if(argobj.ecode!="")
                {
                    if(aDivs[i].getAttribute("cl_code")!=argobj.ecode)
                    {
                        aDivs[i].style.display = "none";
                    }
                    else
                    {
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","");
                        CallMethod("nav_021_gloss","Click",oArgs);
                    }
                }
                else
                {
                    if(aDivs[i].innerHTML!=argobj.eitem)
                    {
                        aDivs[i].style.display = "none";
                    }
                    else
                    {
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","");
                        CallMethod("nav_021_gloss","Click",oArgs);
                    }
                }
            }
            return true;
        }

function Highlight(argobj)
        {
            if(argobj.pid==null || argobj.elem==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;

            var oElem = argobj.elem;
            var sState = oElem.getAttribute("curstate");
            if(sState=="sel") return false;
            var bHL = sState=="hl";
            oElem.setAttribute("curstate", bHL ? "" : "hl");

            var oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font");
            if(oNode!=null && oNode.text!=null) oElem.style.fontFamily = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_size");
            if(oNode!=null && oNode.text!=null) oElem.style.fontSize = oNode.text + "px";
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_weight");
            if(oNode!=null && oNode.text!=null) oElem.style.fontWeight = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_style");
            if(oNode!=null && oNode.text!=null) oElem.style.fontStyle = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_underline");
            if(oNode!=null && oNode.text!=null) oElem.style.textDecoration = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "font_color");
            if(oNode!=null && oNode.text!=null) oElem.style.color = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "bg_color");
            if(oNode!=null && oNode.text!=null) oElem.style.backgroundColor = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "border_color");
            if(oNode!=null && oNode.text!=null) oElem.style.borderColor = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "border_style");
            if(oNode!=null && oNode.text!=null) oElem.style.borderStyle = oNode.text;
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "border_width");
            if(oNode!=null && oNode.text!=null) oElem.style.borderWidth = oNode.text + "px";
            oNode = oParams.selectSingleNode((bHL ? "" : "highlighted_") + "bg_img");
            if(oNode!=null && oNode.text!=null)
            {
                var sBGImage = oNode.text;
                if(sBGImage!="")    oElem.style.backgroundImage = "url(" + sBGImage + ")";
            }
            return true;
        }

function Reset(argobj)
        {
            if(argobj.pid==null || argobj.target==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            var bItems = argobj.target.indexOf("items")!=-1;
            var bLetters = argobj.target.indexOf("letters")!=-1;
            var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
            if(oParams==null) return false;
            var bDisplayLetters = oParams.selectSingleNode("display_letters").text=="yes";

            var oObj = document.getElementById(argobj.pid+"_object_window_clone");

            var oArgs = new Object;
            oArgs.pid = argobj.pid;

            if(bItems)
            {
                var aDivs = oObj.getElementsByTagName("div");
                var oResDiv;
                var oTermsDiv;
                for(var i=0;i<aDivs.length;i++)
                {
                    if(aDivs[i].getAttribute("cl_id")=="results")
                    {
                        oResDiv = aDivs[i];
                        continue;
                    }
                    if(aDivs[i].getAttribute("cl_id")=="terms")
                    {
                        oTermsDiv = aDivs[i];
                        continue;
                    }
                }
                aDivs = oTermsDiv.getElementsByTagName("div");
                for(var i=0;i<aDivs.length;i++)
                {
                    if(aDivs[i].getAttribute("curstate")=="sel")
                    {
                        oArgs.elem = aDivs[i];
                        aDivs[i].setAttribute("curstate","hl");
                        CallMethod("nav_021_gloss","Highlight",oArgs);
                    }
                }
            }

            if(bLetters && bDisplayLetters)
            {
                var aTRs = oObj.getElementsByTagName("tr");
                var oTR;
                for(var i=0;i<aTRs.length;i++)
                {
                    if(aTRs[i].getAttribute("cl_id")=="letters_row")
                    {
                        oTR = aTRs[i];
                        break;
                    }
                }
                var aTDs = oTR.getElementsByTagName("td");
                for(var i=0;i<aTDs.length;i++)
                {
                    if(aTDs[i].getAttribute("curstate")=="sel")
                    {
                        oArgs.elem = aTDs[i];
                        aTDs[i].setAttribute("curstate","hl");
                        CallMethod("nav_021_gloss","Highlight",oArgs);
                    }
                }
            }
            return true;
        }

function GetProperty(argobj)
        {
            if(argobj.pid==null || argobj.property==null) return false;
            var oCurObj = document.getElementById(argobj.pid);
            if(oCurObj==null) return false;
            switch(argobj.property.toLowerCase())
            {
                case "disabled":    g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";    break;
                case "open":        g_vPropertyValue = oCurObj.getAttribute("closed")=="1" ? "0" : "1";    break;
                default: return true;
            }
            return true;
        }

function Over(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                if(oCurObj.getAttribute("winstatus")!="closed") return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                CallMethod("nav_021_gloss", "PlaySound", { pid: argobj.pid, snd: "over" });
                return true;
            }

function Out(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                if(oCurObj.getAttribute("winstatus")!="closed") return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                return true;
            }

function Down(argobj)
            {
                if(argobj.pid==null || argobj.btn==null || argobj.rep==null) return false;
                var oButton = argobj.btn;
                var oReplace = document.getElementById(argobj.pid + argobj.rep);
                if(oButton==null || oReplace==null) return false;
                oButton.src = oReplace.src;
                return true;
            }

function BtnClick(argobj)
            {
                if(argobj.pid==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                CallMethod("nav_021_gloss", "ToggleWindow", { pid: argobj.pid });
                return true;
            }

function PlaySound(argobj)
            {
                if(argobj.pid==null || argobj.snd==null) return false;
                var oCurObj = document.getElementById(argobj.pid);
                if(oCurObj==null) return false;
                var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
                if(oParams==null) return false;
                if(oParams.selectSingleNode("sound.enabled").text=="yes")
                {
                    try
                    {
                        switch(argobj.snd)
                        {
                            case "over":
                                if(oParams.selectSingleNode("sound.scheme").text=="over" || oParams.selectSingleNode("sound.scheme").text=="overclick")
                                {
                                    var sSoundFile = oParams.selectSingleNode("sound.file.over").text;
                                    if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.over.user").text!="")
                                    {
                                        PlaySoundInternal(oParams.selectSingleNode("sound.file.over.user").text);
                                    }
                                    else
                                    {
                                        var sExtension = ".swf";
                                        if(sSoundFile=="custom") sSoundFile = "over1";
                                        PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
                                    }
                                }
                                break;
                            case "click":
                                if(oParams.selectSingleNode("sound.scheme").text=="click" || oParams.selectSingleNode("sound.scheme").text=="overclick")
                                {
                                    var sSoundFile = oParams.selectSingleNode("sound.file.click").text;
                                    if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.click.user").text!="")
                                    {
                                        PlaySoundInternal(oParams.selectSingleNode("sound.file.click.user").text);
                                    }
                                    else
                                    {
                                        var sExtension = ".swf";
                                        if(sSoundFile=="custom") sSoundFile = "click1";
                                        PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
                                    }
                                }
                                break;
                            default: return true;
                        }
                    }
                    catch(e)
                    {}
                }
                return true;
            }

function Constructor(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					var sNavLock = oParams.selectSingleNode("nav_lock").text=="yes" ? "1" : "0";
					oCurObj.setAttribute("navlock", sNavLock);
					oCurObj.setAttribute("state", "enabled");
					AddEvtHandler("EVENT_SLIDE_OPENED", "nav_013_restart", argobj.pid);
					AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_013_restart", argobj.pid);
					AddEvtHandler("EVENT_NAVIGATION", "nav_013_restart", argobj.pid);
					return true;
				}

function HandleEvt(oEvtHandler, oArg)
				{
					var argobj = new Object;
					argobj.pid = oEvtHandler.sTargetId;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var sCurrentSlideId = g_oSlide.getAttribute("id");
					var sSavedSlideId = oCurObj.getAttribute("sid");
					if(sSavedSlideId==null) sSavedSlideId = "";
					switch (oEvtHandler.sEvt) {
						case "EVENT_SLIDE_OPENED":
							if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
							if(oCurObj.getAttribute("navlock")=="1") CallMethod("nav_013_restart","DisableAccess",argobj);
							break;
						case "EVENT_SLIDE_COMPLETE":
							if(oCurObj.getAttribute("navlock")=="1" && sSavedSlideId=="") {
								oCurObj.setAttribute("sid","");
								CallMethod("nav_013_restart","EnableAccess",argobj);
							}
							break;
						case "EVENT_NAVIGATION":
							if(oArg==null) return false;
							if(oArg.sTargets=="restart" || oArg.sTargets=="all") {
								if(oArg.sAction=="on") {
									oCurObj.setAttribute("sid","");
									CallMethod("nav_013_restart","EnableAccess",argobj);
								}
								if(oArg.sAction=="off") {
									oCurObj.setAttribute("sid",sCurrentSlideId);
									CallMethod("nav_013_restart","DisableAccess",argobj);
								}
							}
							break;
						default:
							break;
					}
					return true;
				}

function DisableAccess(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
					if(!bEnabled) return false;
					document.getElementById(argobj.pid+"_restart_en").style.display = "none";
					document.getElementById(argobj.pid+"_restart_dis").style.display = g_isMSIE ? "inline" : "block";
					oCurObj.setAttribute("state","disabled");
					return true;
				}

function EnableAccess(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var bEnabled = oCurObj.getAttribute("state")=="enabled" ? true : false;
					if(bEnabled) return false;
					document.getElementById(argobj.pid+"_restart_dis").style.display = "none";
					document.getElementById(argobj.pid+"_restart_en").style.display = g_isMSIE ? "inline" : "block";
					oCurObj.setAttribute("state","enabled");
					return true;
				}

function Restart(argobj)
				{
					if(argobj.pid==null) return false;
					if(g_oSlide==null) return false;
					OpenSlideById(g_oSlide.getAttribute("id"));
					return true;
				}

