function PlaySound(argobj)
				{
					if(argobj.pid==null || argobj.snd==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					if(oParams.selectSingleNode("sound.enabled").text=="yes")
					{
						try
						{
							switch(argobj.snd)
							{
								case "over":
									if(oParams.selectSingleNode("sound.scheme").text=="over" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.over").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.over.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.over.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "over1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								case "click":
									if(oParams.selectSingleNode("sound.scheme").text=="click" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.click").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.click.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.click.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "click1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								default: return true;
							}
						}
						catch(e)
						{}
					}
					return true;
				}

function Constructor(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    var bNavLock = (oParams.selectSingleNode("nav_lock").text=="yes");
                    oCurObj.setAttribute("state", "enabled");
                    oCurObj.setAttribute("navlock", bNavLock ? "1" : "0");
                    AddEvtHandler("EVENT_SLIDE_OPENED", "nav_009_prev", argobj.pid);
                    AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_009_prev", argobj.pid);
                    AddEvtHandler("EVENT_NAVIGATION", "nav_009_prev", argobj.pid);
                    return true;
                }

function HandleEvt(oEvtHandler)
                {
                    var argobj = new Object;
                    argobj.pid = oEvtHandler.sTargetId;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var sCurrentSlideId = g_oSlide.getAttribute("id");
                    var sSavedSlideId = oCurObj.getAttribute("sid");
                    if(sSavedSlideId==null) sSavedSlideId = "";
                    switch (oEvtHandler.sEvt)
                    {
                        case "EVENT_SLIDE_OPENED":
                            if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
                            CallMethod("nav_009_prev", (oCurObj.getAttribute("navlock")=="1") ? "DisableAccess" : "EnableAccess", argobj);
                            break;
                        case "EVENT_SLIDE_COMPLETE":
                            if(oCurObj.getAttribute("navlock")=="1" && sSavedSlideId=="")
                            {
                                oCurObj.setAttribute("sid", "");
                                CallMethod("nav_009_prev", "EnableAccess", argobj);
                            }
                            break;
                        case "EVENT_NAVIGATION":
                            if(oArg==null) return false;
                            if(oArg.sTargets=="prev" || oArg.sTargets=="all" || oArg.sTargets=="buttons")
                            {
                                if(oArg.sAction=="on")
                                {
                                    oCurObj.setAttribute("sid", "");
                                    CallMethod("nav_009_prev", "EnableAccess", argobj);
                                }
                                if(oArg.sAction=="off")
                                {
                                    oCurObj.setAttribute("sid", sCurrentSlideId);
                                    CallMethod("nav_009_prev", "DisableAccess", argobj);
                                }
                            }
                            break;
                        default:
                            break;
                    }
                    return true;
                }

function DisableAccess(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oEnabled = document.getElementById(argobj.pid + "_PrevEnabled");
                    var oDisabled = document.getElementById(argobj.pid + "_PrevDisabled")
                    if(oEnabled==null || oDisabled==null) return false;
                    oEnabled.style.display = "none";
                    oDisabled.style.display = g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("state","disabled");
                    return true;
                }

function EnableAccess(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    if(oParams==null) return false;
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);

                    if(bStructure)
                    {
                        var sStructureId = oStructure.getAttribute("id");
                        var sCurrentSlideId = g_oSlide.getAttribute("id");
                        var oStructureArgs = { pid: sStructureId, current: sCurrentSlideId, dir: "prev" };
                        CallMethod("nav_025_structure", "GetSlideFromStructure", oStructureArgs);
                        var sTargetSlideId = g_arMethodReturnValue[sStructureId];
                        if(sTargetSlideId==null)
                        {
                            CallMethod("nav_009_prev", "DisableAccess", argobj);
                            return false;
                        }
                    }
                    else
                    {
                        var oPrevSlide = GetSibling(g_oSlide, "prev");
                        if(oPrevSlide==null)
                        {
                            CallMethod("nav_009_prev", "DisableAccess", argobj);
                            return false;
                        }
                    }
                    var oEnabled = document.getElementById(argobj.pid + "_PrevEnabled");
                    var oDisabled = document.getElementById(argobj.pid + "_PrevDisabled")
                    if(oEnabled==null || oDisabled==null) return false;
                    oDisabled.style.display = "none";
                    oEnabled.style.display = g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("state","enabled");
                    return true;
                }

function JumpPrev(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    if(oParams==null) return false;
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);

                    if(bStructure)
                    {
                        var sStructureId = oStructure.getAttribute("id");
                        var sCurrentSlideId = g_oSlide.getAttribute("id");
                        var oStructureArgs = { pid: sStructureId, current: sCurrentSlideId, dir: "prev" };
                        CallMethod("nav_025_structure", "GetSlideFromStructure", oStructureArgs);
                        var sTargetSlideId = g_arMethodReturnValue[sStructureId];
                        if(sTargetSlideId==null) return false;
                        CallMethod("nav_009_prev", "DisableAccess", argobj);
                        OpenSlideById(sTargetSlideId);
                    }
                    else
                    {
                        var oPrevSlide = GetSibling(g_oSlide, "prev");
                        if(oPrevSlide==null) return false;
                        CallMethod("nav_009_prev","DisableAccess",argobj);
                        PreviousSlide();
                    }
					CallMethod("nav_009_prev", "PlaySound", { pid: argobj.pid, snd: "click" });
                    return true;
                }

function GetProperty(argobj)
                {
                    if(argobj.pid==null || argobj.property==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    switch(argobj.property.toLowerCase())
                    {
                        case "disabled":    g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";    break;
                        default: return true;
                    }
                    return true;
                }

function Over(argobj)
				{
					if(argobj.pid==null) return false;
					if(document.getElementById(argobj.pid+"_PrevImg")==null) return false;
					if(document.getElementById(argobj.pid+"_Prev_1")==null) return false;
					if(document.getElementById(argobj.pid+"_Prev_1").src=="") return false;
					document.getElementById(argobj.pid+"_PrevImg").src = document.getElementById(argobj.pid+"_Prev_1").src;
					CallMethod("nav_009_prev", "PlaySound", { pid: argobj.pid, snd: "over" });
					return true;
				}

function Out(argobj)
				{
					if(argobj.pid==null) return false;
					if(document.getElementById(argobj.pid+"_PrevImg")==null) return false;
					if(document.getElementById(argobj.pid+"_Prev_0")==null) return false;
					if(document.getElementById(argobj.pid+"_Prev_0").src=="") return false;
					document.getElementById(argobj.pid+"_PrevImg").src = document.getElementById(argobj.pid+"_Prev_0").src;
					return true;
				}

function Click(argobj)
				{
					if(argobj.pid==null) return false;
					CallMethod("nav_009_prev", "Out", { pid: argobj.pid });
					CallMethod("nav_009_prev", "JumpPrev", { pid: argobj.pid });
					return true;
				}

function Down(argobj)
				{
					if(argobj.pid==null) return false;
					if(document.getElementById(argobj.pid+"_PrevImg")==null) return false;
					if(document.getElementById(argobj.pid+"_Prev_3")==null) return false;
					if(document.getElementById(argobj.pid+"_Prev_3").src=="") return false;
					document.getElementById(argobj.pid+"_PrevImg").src = document.getElementById(argobj.pid+"_Prev_3").src;
					return true;
				}

function PlaySound(argobj)
				{
					if(argobj.pid==null || argobj.snd==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					if(oParams.selectSingleNode("sound.enabled").text=="yes")
					{
						try
						{
							switch(argobj.snd)
							{
								case "over":
									if(oParams.selectSingleNode("sound.scheme").text=="over" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.over").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.over.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.over.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "over1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								case "click":
									if(oParams.selectSingleNode("sound.scheme").text=="click" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.click").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.click.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.click.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "click1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								default: return true;
							}
						}
						catch(e)
						{}
					}
					return true;
				}

function Constructor(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
                    var bNavLock = (oParams.selectSingleNode("nav_lock").text=="yes");
                    oCurObj.setAttribute("state","enabled");
                    oCurObj.setAttribute("navlock", bNavLock ? "1" : "0");
                    AddEvtHandler("EVENT_SLIDE_OPENED", "nav_008_next", argobj.pid);
                    AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_008_next", argobj.pid);
                    AddEvtHandler("EVENT_NAVIGATION", "nav_008_next", argobj.pid);
                    return true;
                }

function HandleEvt(oEvtHandler, oArg)
                {
                    var argobj = new Object;
                    argobj.pid = oEvtHandler.sTargetId;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var sCurrentSlideId = g_oSlide.getAttribute("id");
                    var sSavedSlideId = oCurObj.getAttribute("sid");
                    if(sSavedSlideId==null) sSavedSlideId = "";
                    switch (oEvtHandler.sEvt)
                    {
                        case "EVENT_SLIDE_OPENED":
                            if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid","");
                            CallMethod("nav_008_next", (oCurObj.getAttribute("navlock")=="1") ? "DisableAccess" : "EnableAccess", argobj);
                            break;
                        case "EVENT_SLIDE_COMPLETE":
                            if(oCurObj.getAttribute("navlock")=="1" && sSavedSlideId=="")
                            {
                                oCurObj.setAttribute("sid","");
                                CallMethod("nav_008_next","EnableAccess",argobj);
                            }
                            break;
                        case "EVENT_NAVIGATION":
                            if(oArg==null) return false;
                            if(oArg.sTargets=="next" || oArg.sTargets=="all" || oArg.sTargets=="buttons")
                            {
                                if(oArg.sAction=="on")
                                {
                                    oCurObj.setAttribute("sid", "");
                                    CallMethod("nav_008_next", "EnableAccess", argobj);
                                }
                                if(oArg.sAction=="off")
                                {
                                    oCurObj.setAttribute("sid", sCurrentSlideId);
                                    CallMethod("nav_008_next", "DisableAccess", argobj);
                                }
                            }
                            break;
                        default:
                            break;
                    }
                    return true;
                }

function DisableAccess(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oEnabled = document.getElementById(argobj.pid+"_NextEnabled");
                    var oDisabled = document.getElementById(argobj.pid+"_NextDisabled");
                    if(oEnabled==null || oDisabled==null) return false;
                    oEnabled.style.display = "none";
                    oDisabled.style.display = g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("state","disabled");
                    return true;
                }

function EnableAccess(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    if(oParams==null) return false;
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);

                    if(bStructure)
                    {
                        var sStructureId = oStructure.getAttribute("id");
                        var sCurrentSlideId = g_oSlide.getAttribute("id");
                        var oStructureArgs = { pid: sStructureId, current: sCurrentSlideId, dir: "next" };
                        CallMethod("nav_025_structure", "GetSlideFromStructure", oStructureArgs);
                        var sTargetSlideId = g_arMethodReturnValue[sStructureId];
                        if(sTargetSlideId==null)
                        {
                            CallMethod("nav_008_next", "DisableAccess", argobj);
                            return false;
                        }
                    }
                    else
                    {
                        var oNextSlide = GetSibling(g_oSlide, "next");
                        if(oNextSlide==null)
                        {
                            CallMethod("nav_008_next", "DisableAccess", argobj);
                            return false;
                        }
                    }
                    var oEnabled = document.getElementById(argobj.pid+"_NextEnabled");
                    var oDisabled = document.getElementById(argobj.pid+"_NextDisabled");
                    if(oEnabled==null || oDisabled==null) return false;
                    oDisabled.style.display = "none";
                    oEnabled.style.display = g_isMSIE ? "inline" : "block";
                    oCurObj.setAttribute("state","enabled");
                    return true;
                }

function JumpNext(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    if(oParams==null) return false;
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);

                    if(bStructure)
                    {
                        var sStructureId = oStructure.getAttribute("id");
                        var sCurrentSlideId = g_oSlide.getAttribute("id");
                        var oStructureArgs = { pid: sStructureId, current: sCurrentSlideId, dir: "next" };
                        CallMethod("nav_025_structure", "GetSlideFromStructure", oStructureArgs);
                        var sTargetSlideId = g_arMethodReturnValue[sStructureId];
                        if(sTargetSlideId==null) return false;
                        CallMethod("nav_008_next", "DisableAccess", argobj);
                        OpenSlideById(sTargetSlideId);
                    }
                    else
                    {
                        var oNextSlide = GetSibling(g_oSlide, "next");
                        if(oNextSlide==null) return false;
                        CallMethod("nav_008_next", "DisableAccess", argobj);
                        NextSlide();
                    }
					CallMethod("nav_008_next", "PlaySound", { pid: argobj.pid, snd: "click" });
                    return true;
                }

function GetProperty(argobj)
                {
                    if(argobj.pid==null || argobj.property==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    switch(argobj.property.toLowerCase())
                    {
                        case "disabled":    g_vPropertyValue = oCurObj.getAttribute("state")=="disabled" ? "1" : "0";    break;
                        default: return true;
                    }
                    return true;
                }

function Over(argobj)
				{
					if(argobj.pid==null) return false;
					if(document.getElementById(argobj.pid+"_NextImg")==null) return false;
					if(document.getElementById(argobj.pid+"_Next_1")==null) return false;
					if(document.getElementById(argobj.pid+"_Next_1").src=="") return false;
					document.getElementById(argobj.pid+"_NextImg").src = document.getElementById(argobj.pid+"_Next_1").src;
					CallMethod("nav_008_next", "PlaySound", { pid: argobj.pid, snd: "over" });
					return true;
				}

