function PlaySound(argobj)
				{
					if(argobj.pid==null || argobj.snd==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					if(oParams.selectSingleNode("sound.enabled").text=="yes")
					{
						try
						{
							switch(argobj.snd)
							{
								case "over":
									if(oParams.selectSingleNode("sound.scheme").text=="over" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.over").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.over.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.over.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "over1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								case "click":
									if(oParams.selectSingleNode("sound.scheme").text=="click" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.click").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.click.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.click.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "click1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								default: return true;
							}
						}
						catch(e)
						{}
					}
					return true;
				}

function Constructor(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    if(oParams==null) return false;
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);
                    var bStandard = (oParams.selectSingleNode("standard").text=="yes");
                    if(oParams.selectSingleNode("show_mouseover").text=="yes") document.getElementById(argobj.pid + "_mouseover").innerHTML = "";
                    var oCurRail = document.getElementById(argobj.pid + "_rail");
                    var oCurSrc = document.getElementById(argobj.pid + "_u_tmpl");
                    if(bStandard && !g_isMSIE)
                    {
                        oCurSrc.style.width = parseInt(oCurSrc.style.width) - 2*parseInt(oCurSrc.style.borderWidth) + "px";
                        oCurSrc.style.height = parseInt(oCurSrc.style.height) - 2*parseInt(oCurSrc.style.borderWidth) + "px";
                        oCurSrc.style.cursor = "pointer";
                    }
                    var aSlides = g_oSlides.selectNodes("slide");
                    var oSlide;
                    var sId = "";
                    var sName = "";
                    var oTD;
                    var oImg;
                    if(bStructure)
                    {
                        var sDisplayScope = oParams.selectSingleNode("display_scope").text;
						var sStructureId = oStructure.getAttribute("id");
                        var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId +  "']");
                        if(oStructureParams==null) return false;

                        var aChapters = oStructureParams.selectNodes("chapter_list/item");
                        var aSlideStrip = new Array();
                        var aChapterSlides;
						var aChapterLength = new Array();
						var iIndex = 0;
                        for(var i=0; i<aChapters.length; i++)
                        {
                            if(sDisplayScope=="chapter") iIndex = 0;
							sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
                            if(sId!="")
							{
								iIndex++;
								aSlideStrip.push( {sid: sId, num: iIndex, chap: i});
							}
                            aChapterSlides = aChapters[i].selectNodes("slide_list/item");
                            for(var j=0; j<aChapterSlides.length; j++)
                            {
                                sId = aChapterSlides[j]. selectSingleNode("slide_id").text;
                                if(sId!="")
								{
									iIndex++;
									aSlideStrip.push( {sid: sId, num: iIndex, chap: i} );
								}
                            }
							if(i==0 || sDisplayScope=="chapter")
							{
								aChapterLength[i] = iIndex;
							}
							else
							{
								aChapterLength[i] = iIndex - aChapterLength[i-1];
							}
                        }
                        for(var i=0; i<aSlideStrip.length; i++)
                        {
                            sId = aSlideStrip[i].sid;
							iIndex = aSlideStrip[i].num;
                            oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
                            sName = oSlide.getAttribute("name");
                            oTD = document.createElement("td");
							oTD.setAttribute("id", argobj.pid + "_td_" + sId);
                            oImg = oCurSrc.cloneNode(true);
                            oImg.setAttribute("id", argobj.pid + "_i_" + sId);
                            oImg.setAttribute("sid", sId);
                            oImg.setAttribute("num", iIndex);
                            oImg.setAttribute("total", sDisplayScope=="chapter" ? aChapterLength[aSlideStrip[i].chap] : aSlideStrip.length);
                            oImg.setAttribute("sname", sName);
                            oTD.appendChild(oImg);
                            oCurRail.appendChild(oTD);
                        }
                    }
                    else
                    {
                        for(var i=0; i<aSlides.length;i++)
                        {
                            oSlide = aSlides[i];
                            sId = oSlide.getAttribute("id");
                            sName = oSlide.getAttribute("name");
                            oTD = document.createElement("td");
							oTD.setAttribute("id", argobj.pid + "_td_" + sId);
                            oImg = oCurSrc.cloneNode(true);
                            oImg.setAttribute("id", argobj.pid + "_i_" + sId);
                            oImg.setAttribute("sid", sId);
                            oImg.setAttribute("sname", sName);
                            oImg.setAttribute("num", i+1);
                            oImg.setAttribute("total", aSlides.length);
                            oTD.appendChild(oImg);
                            oCurRail.appendChild(oTD);
                        }
                    }
                    AddEvtHandler("EVENT_SLIDE_OPENED", "nav_015_progress", argobj.pid);
                    AddEvtHandler("EVENT_SLIDE_COMPLETE", "nav_015_progress", argobj.pid);
                    AddEvtHandler("EVENT_NAVIGATION", "nav_015_progress", argobj.pid);
                    return true;
                }

function HandleEvt(oEvtHandler)
                {
                    var argobj = new Object;
                    argobj.pid = oEvtHandler.sTargetId;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var sCurrentSlideId = g_oSlide.getAttribute("id");
                    var sSavedSlideId = oCurObj.getAttribute("sid");
                    if(sSavedSlideId==null) sSavedSlideId = "";

                    switch(oEvtHandler.sEvt)
                    {
                        case "EVENT_SLIDE_OPENED":
							var oArgs = { pid: argobj.pid };
							CallMethod("nav_015_progress", "UpdateProgress", argobj);
                            break;
                        case "EVENT_SLIDE_COMPLETE":
                            if(sSavedSlideId=="") oCurObj.setAttribute("sid", "");
                            break;
                        case "EVENT_NAVIGATION":
                            if(oArg==null) return false;
                            if(oArg.sTargets=="all")
                            {
                                if(oArg.sAction=="on") oCurObj.setAttribute("sid","");
                                if(oArg.sAction=="off") oCurObj.setAttribute("sid",sCurrentSlideId);
                            }
                            break;
                        default:
                            break;
                    }
                }

function UpdateProgress(argobj)
                {
                    if(argobj.pid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
                    if(oParams==null) return false;
                    if(g_oSlide==null) return false;
					var sCurrentSlideId = g_oSlide.getAttribute("id");
                    var sSavedSlideId = oCurObj.getAttribute("sid");
                    if(sSavedSlideId==null) sSavedSlideId = "";
                    var bStandard = (oParams.selectSingleNode("standard").text=="yes");
                    var bSlidename = (oParams.selectSingleNode("show_slidename").text=="yes");
                    var bShownum = (oParams.selectSingleNode("show_number").text=="yes");
                    var oVSrc = document.getElementById(argobj.pid+"_v_tmpl");
                    var oCSrc = document.getElementById(argobj.pid+"_c_tmpl");
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);

                    var aSlides = g_oSlides.selectNodes("slide");
                    var oSlide;
                    var sId = "";
					var sItemId = "";
                    var oTD;
                    var oItem;

					if(bStructure)
					{
                        var sDisplayScope = oParams.selectSingleNode("display_scope").text;
						var sStructureId = oStructure.getAttribute("id");
                        var oStructureParams = g_oParams.selectSingleNode("./param[@objectid='" + sStructureId +  "']");
                        if(oStructureParams==null) return false;

                        var aChapters = oStructureParams.selectNodes("chapter_list/item");
						if(sDisplayScope=="chapter")
						{
							var iCurrentChapter = -1;
							chapter_loop:
							for(var i=0; i<aChapters.length; i++)
							{
								sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
								if(sId!="")
								{
									if(sId==sCurrentSlideId)
									{
										iCurrentChapter = i;
										break chapter_loop;
									}
								}
								aChapterSlides = aChapters[i].selectNodes("slide_list/item");
								for(var j=0; j<aChapterSlides.length; j++)
								{
									sId = aChapterSlides[j]. selectSingleNode("slide_id").text;
									if(sId!="")
									{
										if(sId==sCurrentSlideId)
										{
											iCurrentChapter = i;
											break chapter_loop;
										}
									}
								}
							}
						}
                        var aChapterSlides;
						var aSlideStrip = new Array();
                        for(var i=0; i<aChapters.length; i++)
                        {
                            sId = aChapters[i].selectSingleNode("chapter_title_slide").text;
                            if(sId!="") aSlideStrip.push( { sid: sId, cid: i} );
                            aChapterSlides = aChapters[i].selectNodes("slide_list/item");
                            for(var j=0; j<aChapterSlides.length; j++)
                            {
                                sId = aChapterSlides[j]. selectSingleNode("slide_id").text;
                                if(sId!="") aSlideStrip.push({ sid: sId, cid: i});
                            }
                        }
						var sCurrentSavedId = oCurObj.getAttribute("curid");
                        for(var i=0; i<aSlideStrip.length; i++)
                        {
                            sId = aSlideStrip[i].sid;
							iChapterId = aSlideStrip[i].cid;
							oTD = document.getElementById(argobj.pid + "_td_" + sId);
							oItem = document.getElementById(argobj.pid + "_i_" + sId);
							oSlide = g_oSlides.selectSingleNode("slide[@id='" + sId + "']");
							if(oTD==null || oItem==null || oSlide==null) continue;
							if(SCOIsSlideVisited(oSlide))
							{
								if(!bStandard)
								{
									oItem.src = oVSrc.src;
								}
								else
								{
									oItem.style.backgroundColor = oVSrc.style.backgroundColor;
									oItem.style.borderColor = oVSrc.style.borderColor;
									oItem.style.borderWidth = oVSrc.style.borderWidth;
								}
							}
							if(sId==sCurrentSlideId)
							{
								if(!bStandard)
								{
									oItem.src = oCSrc.src;
								}
								else
								{
									oItem.style.backgroundColor = oCSrc.style.backgroundColor;
									oItem.style.borderColor = oCSrc.style.borderColor;
									oItem.style.borderWidth = oCSrc.style.borderWidth;
								}
							}
							if(sDisplayScope=="chapter")
							{
								oTD.style.display = (iChapterId==iCurrentChapter) ? (g_isMSIE ? "inline" : "block") : "none";
							}
                        }
						oCurObj.setAttribute("curid", sCurrentSlideId);
						oItem = document.getElementById(argobj.pid + "_i_" + sCurrentSlideId);
						if(bSlidename)
						{
							if(g_oSlide==null) return false;
							if(bShownum)
							{
								document.getElementById(argobj.pid + "_current").innerHTML = oItem.getAttribute("num") + "/" + oItem.getAttribute("total") + " " + g_oSlide.getAttribute("name");
							}
							else
							{
								document.getElementById(argobj.pid + "_current").innerHTML = g_oSlide.getAttribute("name");
							}
						}
					}
					else
					{
						for(var i=0;i<aSlides.length;i++)
						{
							oSlide = aSlides[i];
							if(!SCOIsSlideVisited(oSlide)) continue;
							sId = oSlide.getAttribute("id");
							sItemId = argobj.pid + "_i_" + sId;
							oItem = document.getElementById(sItemId);
							if(!bStandard)
							{
								oItem.src = oVSrc.src;
							}
							else
							{
								oItem.style.backgroundColor = oVSrc.style.backgroundColor;
								oItem.style.borderColor = oVSrc.style.borderColor;
								oItem.style.borderWidth = oVSrc.style.borderWidth;
							}
						}
						if(sSavedSlideId!=sCurrentSlideId) oCurObj.setAttribute("sid", "");
						sItemId = argobj.pid + "_i_" + oCurObj.getAttribute("curid");
						oItem = document.getElementById(sItemId);
						if(oCurObj.getAttribute("curid")!=null)
						{
							if(!bStandard)
							{
								oItem.src = oVSrc.src;
							}
							else
							{
								oItem.backgroundColor = oVSrc.style.backgroundColor;
								oItem.style.borderColor = oVSrc.style.borderColor;
								oItem.style.borderWidth = oVSrc.style.borderWidth;
							}
						}
						oCurObj.setAttribute("curid", sCurrentSlideId);
						sItemId = argobj.pid + "_i_" + sCurrentSlideId;
						oItem = document.getElementById(sItemId);
						if(!bStandard)
						{
							oItem.src = oCSrc.src;
						}
						else
						{
							oItem.style.backgroundColor = oCSrc.style.backgroundColor;
							oItem.style.borderColor = oCSrc.style.borderColor;
							oItem.style.borderWidth = oCSrc.style.borderWidth;
						}
						if(bSlidename)
						{
							if(g_oSlide==null) return false;
							if(bShownum)
							{
								document.getElementById(argobj.pid + "_current").innerHTML = oItem.getAttribute("num") + "/" + oItem.getAttribute("total") + " " + g_oSlide.getAttribute("name");
							}
							else
							{
								document.getElementById(argobj.pid + "_current").innerHTML = g_oSlide.getAttribute("name");
							}
						}

                     }
                }

function JumpToSlide(argobj)
                {
                    if(argobj.pid==null || argobj.jid==null) return false;
                    var oCurObj = document.getElementById(argobj.pid);
                    if(oCurObj==null) return false;
                    var sSavedSlideId = oCurObj.getAttribute("sid");
                    if(sSavedSlideId!="") return false;
                    var sJumpTo = argobj.jid;
                    if(sJumpTo==g_oSlide.getAttribute("id")) return false;
                    var oParams = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid+"']");
                    if(oParams==null) return false;
                    // Detecting structure object
                    var oStructure = g_oSlides.selectSingleNode("//object[@type='nav_025_structure']");
                    var bStructure = (oStructure!=null);
                    var sMsg = oParams.selectSingleNode("msg").text;
                    var sCurrentSlideId = g_oSlide.getAttribute("id");

                    if(bStructure)
                    {
                        var sStructureId = oStructure.getAttribute("id");
                        var oStructureArgs = { pid: sStructureId, current: sCurrentSlideId, target: sJumpTo };
                        CallMethod("nav_025_structure", "CheckAllowance", oStructureArgs);
                        if(g_arMethodReturnValue[sStructureId]==false)
                        {
                            g_arMethodReturnValue[sStructureId] = null;
                            alert(sMsg);
                            return false;
                        }
                        g_arMethodReturnValue[sStructureId] = null;
                        OpenSlideById(sJumpTo);
                        return true;
                    }

                    if(g_bStrictOrder)
                    {
                        var oSlide = g_oSlides.selectSingleNode("slide[@id='" + sJumpTo + "']");
                        var oPrev = GetSibling(oSlide, "prev");
                        if(oPrev==null) return false;
                        if(!SCOIsSlideVisited(oPrev))
                        {
                            alert(sMsg);
                            return false;
                        }
                    }
                    OpenSlideById(sJumpTo);
                    return true;
                }

function Over(argobj)
				{
					if(argobj.pid==null || argobj.overtxt==null) return false;
					var oOverTxt = document.getElementById(argobj.pid + "_mouseover");
					if(oOverTxt==null) return false;
					oOverTxt.innerHTML = argobj.overtxt;
					CallMethod("nav_015_progress", "PlaySound", { pid: argobj.pid, snd: "over" });
					return true;
				}

function Out(argobj)
				{
					if(argobj.pid==null) return false;
					var oOverTxt = document.getElementById(argobj.pid + "_mouseover");
					if(oOverTxt==null) return false;
					oOverTxt.innerHTML = "";
					return true;
				}

function Click(argobj)
				{
					if(argobj.pid==null || argobj.jid==null) return false;
					CallMethod("nav_015_progress", "JumpToSlide", argobj);
					CallMethod("nav_015_progress", "PlaySound", { pid: argobj.pid, snd: "click" });
					return true;
				}

function PlaySound(argobj)
				{
					if(argobj.pid==null || argobj.snd==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					if(oParams.selectSingleNode("sound.enabled").text=="yes")
					{
						try
						{
							switch(argobj.snd)
							{
								case "over":
									if(oParams.selectSingleNode("sound.scheme").text=="over" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.over").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.over.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.over.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "over1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								case "click":
									if(oParams.selectSingleNode("sound.scheme").text=="click" || oParams.selectSingleNode("sound.scheme").text=="overclick")
									{
										var sSoundFile = oParams.selectSingleNode("sound.file.click").text;
										if(sSoundFile=="custom" && oParams.selectSingleNode("sound.file.click.user").text!="")
										{
											PlaySoundInternal(oParams.selectSingleNode("sound.file.click.user").text);
										}
										else
										{
											var sExtension = ".swf";
											if(sSoundFile=="custom") sSoundFile = "click1";
											PlaySoundInternal(g_sCourseImages + sSoundFile + sExtension);
										}
									}
									break;
								default: return true;
							}
						}
						catch(e)
						{}
					}
					return true;
				}

function Constructor(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oCurOn = document.getElementById(argobj.pid+"_sound_on");
					var oCurOff = document.getElementById(argobj.pid+"_sound_off");
					if(!g_bSoundEnabled) {
						oCurOn.style.display = "none";
						oCurOff.style.display = g_isMSIE ? "inline" : "block";
						EnableSound(false);
					} else {
						oCurOff.style.display = "none";
						oCurOn.style.display = g_isMSIE ? "inline" : "block";
					}
					return true;
				}

function toggleSound(argobj)
				{
					if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					CallMethod("nav_011_sound", "PlaySound", { pid: argobj.pid, snd: "click" });
					var oCurOn = document.getElementById(argobj.pid+"_sound_on");
					var oCurOff = document.getElementById(argobj.pid+"_sound_off");
					if(oCurOff.style.display=="none") {
						oCurOn.style.display = "none";
						oCurOff.style.display = g_isMSIE ? "inline" : "block";
						EnableSound(false);
					} else {
						oCurOff.style.display = "none";
						oCurOn.style.display = g_isMSIE ? "inline" : "block";
						EnableSound(true);
					}
					return true;
				}

function GetProperty(argobj)
				{
					if(argobj.pid==null || argobj.property==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oCurOff = document.getElementById(argobj.pid+"_sound_off");
					switch(argobj.property.toLowerCase()) {
						case "soundon":	g_vPropertyValue = oCurOff.style.display=="none" ? "1" : "0";	break;
						default: return true;
					}
					return true;
				}

